mapper.insert:
INSERT INTO user_collection_college (
    user_id,
    college_id
) VALUES (
    #{userId},
    #{collegeId}
 )

mapper.delete:
DELETE
FROM user_collection_college
WHERE user_id = #{userId} AND college_id = #{collegeId}

mapper.select:
SELECT *
FROM user_collection_college
WHERE user_id = #{userId} AND college_id = #{collegeId}
